/**
 * Capitaliza a primeira letra de uma string.
 * @param str A string a ser capitalizada.
 */
export function capitalize(str: string | null | undefined): string;

/**
 * Trunca uma string para um comprimento específico.
 * @param str A string a ser truncada.
 * @param length O comprimento máximo da string.
 */
export function truncate(str: string | null | undefined, length?: number): string;